import React, { Component } from 'react'
import {connect} from "react-redux"
import {Button} from "../../components/Button"

class Order extends Component{
    render(){
        return(
            <div
            style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                height: '90vh'
              }}
            >
                <form>
                        <div className="row-input">
                            <h1 style={{textAlign:"center"}}>BOOK HOTEL NOW</h1>
                        </div>

                        <div className="row-input">
                            <span className="label">Masukan Nama Lengkap: </span>
                            <input type="text" 
                            name="nameValue" 
                            placeholder="Nama Lengkap"
                            // onChange={this.handleChangeName}
                            // value={this.state.name}
                            />
                        </div>

                        <div className="row-input">
                            <span className="label">Masukkan Nomor Telepon : </span>
                            <input type="text" 
                            name="phoneValue" 
                            placeholder="Nomor Telepon"
                            // onChange={this.handleChangePhone}
                            // value={this.state.phone}
                            />
                        </div>

                        <div className="row-input">
                            <span className="label">Masukkan Alamat : </span>
                            <input type="text" 
                            name="addressValue" 
                            placeholder="Alamat Pengguna" 
                            // onChange={this.handleChangeAlamat}
                            // value={this.state.address}
                            />
                        </div>

                        <div className="row-input">
                            <span className="label">Masukkan Username : </span>
                            <input type="text" 
                            name="usernameValue" 
                            placeholder="Input Username"
                            // onChange={this.handleChangeUsername}
                            // value={this.state.username}
                            />
                        </div>

                        <div className="row-input">
                            <span className="label">Masukkan Password : </span>
                            <input type="password" 
                            name="passwordValue" 
                            placeholder="Input Password"
                            // onChange={this.handleChangePassword}
                            // value={this.state.password}
                            />
                        </div>

                        <div className="row-input">
                            <Button>Register</Button>
                        </div>
                </form>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    loginStat: state.AuthStat.login_status,
    orderStat: state.StatusOrder.orderStatus
})


export default connect(mapStateToProps)(Order);
