import React, { Component } from 'react'
import {connect} from "react-redux"
import {Button} from "../../components/Button"

class Order extends Component{
    constructor(props){
        super(props);
        this.state={
            username: "",
            phone: "",
            hotel: "",
            date: "",
            duration:""
        }   

        this.handleChangeUsername = this.handleChangeUsername.bind(this)
        this.handleChangePhone = this.handleChangePhone.bind(this)
        this.handleChangeHotel = this.handleChangeHotel.bind(this)
        this.handleChangeDate = this.handleChangeDate.bind(this)
        this.handleChangeBooking = this.handleChangeBooking.bind(this)

        this.handleSubmit = this.handleSubmit.bind(this)
    }

    // Handle Change
    handleChangeUsername = event => {
        this.setState({username:this.props.orderan.username})
    }
    handleChangePhone = event => {
        this.setState({phone:this.props.loginUser.phone})
    }
    handleChangeHotel = event => {
        this.setState({hotel:this.props.orderan.hotel})
    }
    handleChangeDate = event => {
        this.setState({date:event.target.value})
    }
    handleChangeBooking = event => {
        this.setState({duration:event.target.value})
    }

    // Handle Submit
    handleSubmit = event => {
        event.preventDefault();
        this.submitOrder(
            this.state.username,
            this.state.phone,
            this.state.hotel,
            this.state.date,
            this.state.duration
        )
    }


    getData(){
        this.setState(
            {
                username: this.props.dataSend.name,
                genderValue: this.props.dataSend.gender,
                ageValue: this.props.dataSend.age,
                addressValue: this.props.dataSend.address,
            }
        )
    }

    submitOrder(inputUser, inputPhone, inputHotel, inputDate, inputBooking){
        const date = new Date(inputDate);
        const bookDate = date.getDay() + inputBooking;

        const registerOrder = {
            username: inputUser,
            phone: inputPhone,
            hotel: inputHotel,
            durasi: inputBooking,
            tanggalpesan: bookDate
        }

        console.log("Registered Order : ", registerOrder)

    }

    render(){
        console.log("Data Order : ", this.props.orderStat)
        return(
            <div
            style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                height: '90vh'
              }}
            >
                <form>
                    <div className="row-input">
                        <h1 style={{textAlign:"center"}}>BOOK HOTEL NOW</h1>
                    </div>

                    <div className="row-input">
                        <span className="label">Username : </span>
                        <input type="text" 
                        name="nameValue" 
                        placeholder="Nama Lengkap"
                        onChange={this.handleChangeUsername}
                        value={this.props.orderan.username}
                        />
                    </div>

                    <div className="row-input">
                        <span className="label">Nomor Telepon : </span>
                        <input type="text" 
                        name="phoneValue" 
                        placeholder="Nomor Telepon"
                        onChange={this.handleChangePhone}
                        value={this.props.loginUser.phone}
                        />
                    </div>

                    <div className="row-input">
                        <span className="label">Nama Hotel : </span>
                        <input type="text" 
                        name="hotelValue" 
                        placeholder="Alamat Pengguna" 
                        onChange={this.handleChangeHotel}
                        value={this.props.orderan.hotel}
                        />
                    </div>

                    <div className="row-input">
                        <span className="label">Masukkan Tanggal Booking : </span>
                        <input type="date" 
                        name="dateValue" 
                        onChange={this.handleChangeDate}
                        value={this.state.date}
                        />
                    </div>

                    <div className="row-input">
                        <span className="label">Durasi Booking (per malam) : </span>
                        <input type="numbers" 
                        name="bookValue" 
                        placeholder="Input lama booking"
                        onChange={this.handleChangeBooking}
                        value={this.state.duration}
                        />
                    </div>

                    <div className="row-input">
                        <Button>Order</Button>
                    </div>
                </form>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    loginUser: state.AuthStat.userData,
    orderStat: state.StatusOrder.orderStatus,
    orderan: state.StatusOrder.orderData
})


export default connect(mapStateToProps)(Order);
