import React, {useEffect, useState, Component} from 'react';
import { connect } from 'react-redux';
import { useTable, usePagination } from 'react-table'
import "./Table.css"

class BuildTable extends Component {
    constructor(props){
        super(props);
        this.state = {}
    }

    ambilData(input){
        // console.log("Data : ", input)
        // console.log("Banyak Data : ", input.length)

        return input.map((user, index) => {
            const {id, name, gender, age, address} = user
            if (user.length !== null){
                return(
                    <tr key={index}>
                        <td>{name}</td>
                        <td>{gender}</td>
                        <td>{age}</td>
                        <td>{address}</td>
                        <td>
                            <button onClick={()=>this.editData(id)}>Edit</button>
                            <button onClick={() => this.deleteData(id)}>Delete</button>
                        </td>
                    </tr>
                )
            }
        })
    }

    editData(index){
        this.props.editUser(index)
    }

    render(){
        return(
            <table className="tableUser">
                <thead>
                    <tr>
                        <th>No. </th>
                        <th>NAMA HOTEL</th>
                        <th>KETERANGAN</th>
                    </tr>
                </thead>
                <tbody>
                    {this.ambilData(this.props.usersList)}
                </tbody>
            </table>
        )
    }
}
  
export default BuildTable;
