import React, {useEffect, useState, Component} from 'react';
import { connect } from 'react-redux';
import { useTable, usePagination } from 'react-table'
import "./Table.css"

class BuildTable extends Component {
    constructor(props){
        super(props);
        this.state = {}
    }

    ambilData(input){
        // console.log("Data : ", input)
        // console.log("Banyak Data : ", input.length)

        return input.map((user, index) => {
            const {id, name, kota, kamar, terpakai, tersedia, harga} = user
            if (user.length !== null){
                return(
                    <tr key={index}>
                        <td>
                            {id}
                        </td>
                        <td>
                            {name}
                        </td>
                        <td>
                            <ul>
                                <li><a>Kota : {kota}</a></li>
                                <li><a>Jumlah Kamar : {kamar}</a></li>
                                <li><a>Kamar Terpakai : {terpakai}</a></li>
                                <li><a>Kamar Tersedia : {tersedia}</a></li>
                                <li><a>Harga : {harga}</a></li>
                                <li><button onClick={() => this.orderHotel(id)}>Delete</button></li>
                            </ul>
                        </td>
                    </tr>
                )
            }
        })
    }

    orderHotel(index){
        console.log("Order Hotel : ", index)
        // this.props.editUser(index)
    }

    render(){
        return(
            <table className="tableUser">
                <thead>
                    <tr>
                        <th>No. </th>
                        <th>NAMA HOTEL</th>
                        <th>KETERANGAN</th>
                    </tr>
                </thead>
                <tbody>
                    {this.ambilData(this.props.usersList)}
                </tbody>
            </table>
        )
    }
}
  
export default BuildTable;
