import React, { Component } from 'react'
import {Button} from "../../components/Button"
import "./login.css"
import {connect} from "react-redux"

class Login extends Component {
    constructor(props){
        super(props);
        this.state = {
            username: "",
            password: ""
        }

        this.handleChangeUsername = this.handleChangeUsername.bind(this)
        this.handleChangePassword = this.handleChangePassword.bind(this)

        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleChangeUsername(event){
        this.setState({ username: event.target.value })
    }

    handleChangePassword(event){
        this.setState({ password: event.target.value })
    }

    handleSubmit(event){
        event.preventDefault();

        this.submitFunction(
            this.state.username,
            this.state.password
        );
    }

    submitFunction(inputUser, inputPassword){
        const loginData = {
            username:inputUser,
            password:inputPassword
        }

        console.log("Login User : ", loginData)
        // this.props.loginRespond(loginData)
        // this.props.setLoginStatus()
    }

    render(){
        return(
            <div
                style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                height: '90vh'
                }}
            >
                <form onSubmit={this.handleSubmit}>
                    <ul>
                        <li><h2>Input Username</h2></li>
                        <li>
                            <input 
                            style={{
                                width:"300px", 
                                height:"40px",
                                borderRadius:"45px",
                                border:"1px solid"
                            }}
                            type="text"
                            placeholder="   Input Username   "
                            value={this.state.username}
                            onChange={this.handleChangeUsername}
                            />
                        </li>
                        <li><h2>Input Password</h2></li>
                        <li>
                            <input 
                            style={{
                                width:"300px", 
                                height:"40px",
                                borderRadius:"45px",
                                border:"1px solid"
                            }}
                            type="password"
                            placeholder="    Input Password   "
                            value={this.state.password}
                            onChange={this.handleChangePassword}
                            />
                        </li>
                        <li><Button>Login</Button></li>
                        <li>Don't have an account ? <a>Register Now</a></li>
                    </ul>
                </form>
            </div>
        )
    }
}

const mapDispatchToProps=dispatch=>({
    setLoginStatus: () => dispatch({type:"LOGIN_OK"})
})


export default connect(null, mapDispatchToProps)(Login);
