import React, {Component} from 'react';
import "./Table.css"
import {Button} from "../Button"

class BuildTable extends Component {
    constructor(props){
        super(props);
        this.state = {}
    }

    ambilData(){
        console.log("Data : ", this.props.hotelList);
        return this.props.hotelList.map((user, index) => {
            return(
                <tr key={index}>
                    <td>
                        {user.id}
                    </td>
                    <td>
                        <ul>
                            <li>
                                <img src={user.imgurl} 
                                width={"550px"}
                                height={"250px"}
                                />
                            </li>
                        </ul>
                    </td>
                    <td>
                        <ul>
                        <li>{user.hotelName}</li>
                            <li><a>Kota : {user.kota}</a></li>
                            <li><a>Jumlah Kamar : {user.jumlahKamar}</a></li>
                            <li><a>Kamar Terpakai : {user.terpakai}</a></li>
                            <li><a>Kamar Tersedia : {user.tersedia}</a></li>
                            <li><a>Harga : {user.harga}</a></li>
                            <li>
                                <Button onClick={() => this.orderHotel(user.id)}>
                                    Order
                                </Button>
                                {/* <button onClick={() => this.orderHotel(user.id)}>Order</button> */}
                            </li>
                        </ul>
                    </td>
                </tr>
            )
        })
    }

    orderHotel(index){
        console.log("Order Hotel : ", index)
        // this.props.editUser(index)
    }

    render(){
        return(
            <table>
                <thead>
                    <tr>
                        <th>No. </th>
                        <th>NAMA HOTEL</th>
                        <th>KETERANGAN</th>
                    </tr>
                </thead>
                <tbody>
                    {this.ambilData()}
                </tbody>
            </table>
        )
    }
}

export default BuildTable;
